                    //    </div>
                                <div className="card col-4" style="width: 18rem !important;">
                                    <img src={datum.image} className="img-thumbnail" alt=""/>
                                    <div class="card-body">
                                        <h4 className="card-title">{datum.name}</h4>
                                        <h5 className="card-title">{datum.descpription}</h5>
                                        <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                        <a href="" className="w-75 d-flex justify-content-center btn btn-primary justi">Shop Now</a>
                                    </div>
                                </div>
                )
            })
        }
        </div>
    </div>
  )
}

export default Product ;
